var bearCat = require("bearcat");
var _ = require("lodash");

function application() {

}
application.prototype.init = function (bearCat,components) {
    this.components = components;
    this.bearCat = bearCat;
};


application.prototype.getComponent = function (name) {
    if (this.components[name]) {
        var component = this.bearCat.getBean(name);
        return component;
    } else {
        return null;
    }
};


module.exports = {
    id: "application",
    func: application
};